        if ($(this).val().toLowerCase() !== $('.code').children('input').val().toLowerCase()) {
            alert('验证码不正确')
        } else {
            alert('验证通过')
        }
    })


    $(""#btnZhao"").click(function () {
        var Mito = $(""#txtMi2"").val();
        var yan = $(""#txtYan"").val();

        

        var Phone = $(""#txtDian"").val();
        var Cipher = $(""#txtMi"").val();
        var IDnumber = $(""#txtShen"").val();

        if (Phone == """" || Phone == null) {
            alert('手机号码不能为空');
            return;
        }
        if (Cipher == """" || Cipher == null) {
            alert('密码不能为空！');
            return;
        }
        if (Mito != Cipher) {
            alert('两次密码不一致！');
            return;
        }
        if (IDnumber == """" || IDnumber == null) {
            alert('身份证号码不能为空！'");
            WriteLiteral(@");
            return;
        }
        if (yan == """" || yan == null) {
            alert('验证码不能为空！');
            return;
        }

        $.ajax({
            url: 'http://localhost:8648/api/Amend',
            type: 'post',
            //dataType: 'Json',
            data: { Cipher: Cipher, IDnumber: IDnumber, Phone: Phone },
            success: function (d) {
                if (d > 0) {
                    alert('修改成功！');
                    window.location.href = ""It"";
                }
                else {
                    alert('手机号或身份证号错误！');
